// global vars
const numRounds = 9;

var players = {};
var playerTurn = null;
var turnImages = [];
var image = null;

// reset game condition
function resetGame() {
  players = {};
  playerTurn = null;
  turnImages = [];
  image = null;
}

// chose player turn
var randomPlayer = function(obj) {
  var keys = Object.keys(obj);
  return keys[(keys.length * Math.random()) << 0];
};

// chose next player, obj is players, key is playerTurn?
var next = function(obj, key) {
  var keys = Object.keys(obj)
    , i = keys.indexOf(key);
  return i !== -1 && keys[i + 1] && obj[keys[i + 1]];
};

// chose 4 images at random
var getImages = function(images) {
  var randomImages = images.sort(() => 0.5 - Math.random()).slice(0, 4);
  return randomImages;
};

// listen for reset command
app.message("resetgame", async ({ message, say }) => {
  resetGame();

  await say({
    text: `Game has been reset`
  });
});

// listen for init command
app.message("initgame", async ({ message, say }) => {
  await say({
    blocks: [
      {
        type: "section",
        text: {
          type: "mrkdwn",
          text: `<@${message.user}> would like to start the game, click to join!`
        },
        accessory: {
          type: "button",
          text: {
            type: "plain_text",
            text: "Join game",
            emoji: true
          },
          action_id: "addplayer"
        }
      }
    ]
  });
});

